document.addEventListener("DOMContentLoaded", function () {
  let e = document.querySelectorAll(".nav-link"),
    t = window.location.pathname.split("/").pop();
  e.forEach(function (e) {
    e.getAttribute("href").split("/").pop() === t && e.classList.add("active1");
  });
  let n = document.querySelectorAll(".nav-link"),
    l = document.getElementById("nav-menu"),
    i = document.getElementById("menu-icon-open"),
    c = document.getElementById("menu-icon-close");
  function s() {
    let e = "block" === l.style.display ? "none" : "block";
    (l.style.display = e),
      (i.style.display = "none" === e ? "inline-block" : "none"),
      (c.style.display = "block" === e ? "inline-block" : "none");
  }
  n.forEach((e) => {
    e.addEventListener("click", function () {
      n.forEach((e) => {
        e.classList.remove("active");
      }),
        this.classList.add("active"),
        s();
    }),
      e.href === window.location.href && e.classList.add("active");
  }),
    i.addEventListener("click", s),
    c.addEventListener("click", s);
  let o = document.getElementById("terms"),
    a = document.getElementById("close-terms"),
    d = document.querySelector(".terms .backdrop"),
    r = document.querySelector(".terms-link");
  function y() {
    o.style.display = "none";
  }
  r.addEventListener("click", function (e) {
    e.preventDefault(), (o.style.display = "block");
  }),
    a.addEventListener("click", y),
    d.addEventListener("click", y);
});
